angular.module('n52.core.base')
    .factory('colorService', ['settingsService', 'colorPool',
        function(settingsService, colorPool) {

            var colorList,
                refColorList,
                selectFromList;

            function init() {

                colorList = settingsService.colorList;
                refColorList = settingsService.refColorList || [];
                selectFromList = settingsService.selectColorFromList || false;
                colorPool.setColors(colorList);
                colorPool.setRefColors(refColorList);
            }

            init();

            function _hashCode(str) {
                var hash = 0;
                for (var i = 0; i < str.length; i++) {
                    hash = str.charCodeAt(i) + ((hash << 5) - hash);
                }
                return hash;
            }

            function _intToColorHex(i) {
                var rgb = ((i >> 16) & 0xFF).toString(16) +
                    ((i >> 8) & 0xFF).toString(16) +
                    (i & 0xFF).toString(16);
                rgb = rgb.toString();
                while (rgb.length < 6) {
                    rgb = "0" + rgb;
                }
                return rgb;
            }

            function _stringToColor(string) {
                if (!string)
                    return "#000000";
                return "#" + _intToColorHex(_hashCode(string));
            }

            function getColor(string) {
                var color;
                if (selectFromList) {
                    color = colorPool.getColor();
                }
                return color ? color : _stringToColor(string);
            }

            function getRefColor(string) {
                var color;
                if (selectFromList) {
                    color = colorPool.getRefColor();
                }
                return color ? color : _stringToColor(string);
            }

            function removeColor(color) {
                colorPool.removeColor(color);
            }

            function removeRefColor(color) {
                colorPool.removeRefColor(color);
            }

            return {
                getColor: getColor,
                getRefColor: getRefColor,
                removeColor: removeColor,
                removeRefColor: removeRefColor,
                colorList: colorList
            };
        }
    ])
    .factory('colorPool', [function() {
        var colorPool;
        var refColorPool;

        function _createPool(list) {
            var pool = {};
            angular.forEach(list, function(color) {
                pool[color] = true;
            });
            return pool;
        }

        function _getColorOfPool(pool) {
            var color;
            angular.forEach(pool, function(available, entry) {
                if (!color && available) {
                    color = entry;
                }
            });
            if (color)
                pool[color] = false;
            return color;
        }

        function _removeColorFromPool(color, pool) {
            if (angular.isDefined(pool[color])) {
                pool[color] = true;
            }
        }

        function setColors(list) {
            colorPool = _createPool(list);
        }

        function getColor() {
            return _getColorOfPool(colorPool);
        }

        function removeColor(color) {
            _removeColorFromPool(color, colorPool);
        }

        function setRefColors(list) {
            refColorPool = _createPool(list);
        }

        function getRefColor() {
            return _getColorOfPool(refColorPool);
        }

        function removeRefColor(color) {
            _removeColorFromPool(color, refColorPool);
        }

        return {
            setColors: setColors,
            setRefColors: setRefColors,
            getColor: getColor,
            removeColor: removeColor,
            getRefColor: getRefColor,
            removeRefColor: removeRefColor
        };
    }]);

angular.module('n52.core.base')
    .factory('favoriteService', ['localStorageService', '$translate', 'settingsService', 'seriesApiInterface',
        function(localStorageService, $translate, settingsService, seriesApiInterface) {
            var storageKey = 'favorites';
            var favorites = {};
            var groupIdx = Object.keys(favorites).length;
            if (groupIdx === 0) groupIdx++;

            // load favorites
            setFavorites(localStorageService.get(storageKey));

            function saveFavorites() {
                localStorageService.set(storageKey, favorites);
            }

            function addFavorite(ts, label) {
                label = label || ts.label;
                favorites[ts.apiUrl + ts.id] = {
                    id: ts.apiUrl + ts.id,
                    label: label,
                    type: 'single',
                    timeseries: angular.copy(ts)
                };
                saveFavorites();
                return label;
            }

            function addFavoriteGroup(tsColl, label) {
                var collection = [];
                angular.forEach(tsColl, function(elem) {
                    collection.push(angular.copy(elem));
                });
                if (collection.length !== 0) {
                    label = label || $translate.instant('favorite.label') + ' ' + groupIdx;
                    favorites[groupIdx] = {
                        id: groupIdx,
                        label: label,
                        type: 'group',
                        collection: collection
                    };
                    saveFavorites();
                    groupIdx++;
                    return label;
                } else {
                    return null;
                }
            }

            function removeFavorite(fav) {
                delete favorites[fav.id];
                saveFavorites();
            }

            function hasTimeseriesAsFavorite(ts) {
                return angular.isObject(favorites[ts.apiUrl + ts.id]);
            }

            function hasFavorites() {
                return Object.keys(favorites).length > 0;
            }

            function getFavoritesCount() {
                return Object.keys(favorites).length;
            }

            function removeAllFavorites() {
                angular.forEach(favorites, function(elem) {
                    removeFavorite(elem);
                });
            }

            function setFavorites(newFavs) {
                removeAllFavorites();
                angular.forEach(newFavs, function(fav) {
                    // single
                    if (fav.timeseries) {
                        // send request to get latest value
                        var oldTs = fav.timeseries;
                        seriesApiInterface.getTimeseries(oldTs.id, oldTs.apiUrl).then(function(newTs) {
                            newTs.styles = oldTs.styles;
                            addFavorite(newTs, fav.label);
                        });
                    }
                    // group
                    if (fav.collection) {
                        var count = 0;
                        var newColl = [];
                        angular.forEach(fav.collection, function(ts) {
                            count++;
                            seriesApiInterface.getTimeseries(ts.id, ts.apiUrl).then(function(newTs) {
                                newTs.styles = ts.styles;
                                newColl.push(newTs);
                                count--;
                                if (count === 0) {
                                    addFavoriteGroup(newColl, fav.label);
                                }
                            });
                        });
                    }
                });
                saveFavorites();
            }

            function changeLabel(favorite, label) {
                favorites[favorite.id].label = label;
                saveFavorites();
            }

            return {
                addFavorite: addFavorite,
                addFavoriteGroup: addFavoriteGroup,
                hasTimeseriesAsFavorite: hasTimeseriesAsFavorite,
                hasFavorites: hasFavorites,
                setFavorites: setFavorites,
                removeFavorite: removeFavorite,
                removeAllFavorites: removeAllFavorites,
                changeLabel: changeLabel,
                getFavoritesCount: getFavoritesCount,
                favorites: favorites
            };
        }
    ])
    .factory('favoriteImExportService', ['favoriteService', '$translate', 'alertService', 'utils',
        function(favoriteService, $translate, alertService, utils) {
            function importFavorites(event) {
                if (utils.isFileAPISupported() && angular.isObject(event)) {
                    var override = true;
                    if (favoriteService.hasFavorites()) {
                        override = window.confirm($translate.instant('favorite.import.override'));
                    }
                    if (override) {
                        var files = event.target.files;
                        if (files && files.length > 0) {
                            var reader = new window.FileReader();
                            reader.readAsText(files[0]);
                            reader.onerror = function() {
                                alertService.error($translate.instant('favorite.import.wrongFile'));
                            };
                            reader.onload = $.proxy(function(e) {
                                favoriteService.setFavorites(angular.fromJson(e.target.result));
                            }, this);
                        }
                    }
                }
            }

            function exportFavorites() {
                if (utils.isFileAPISupported()) {
                    var filename = 'favorites.json';
                    var content = angular.toJson(favoriteService.favorites, 2);
                    if (window.navigator.msSaveBlob) {
                        // IE version >= 10
                        var blob = new window.Blob([content], {
                            type: 'application/json;charset=utf-8;'
                        });
                        window.navigator.msSaveBlob(blob, filename);
                    } else {
                        // FF, Chrome ...
                        var a = document.createElement('a');
                        a.href = 'data:application/json,' + encodeURIComponent(content);
                        a.target = '_blank';
                        a.download = filename;
                        document.body.appendChild(a);
                        a.click();
                    }
                }
            }

            return {
                importFavorites: importFavorites,
                exportFavorites: exportFavorites
            };
        }
    ]);

angular.module('n52.core.base')
    .service('permalinkGenerationService', ['$location', 'timeseriesService', 'statusService', 'utils',
        function($location, timeseriesService, statusService, utils) {
            var createTimeseriesParam = function(timeseriesId) {
                var ids = [];
                if (angular.isUndefined(timeseriesId)) {
                    angular.forEach(timeseriesService.getAllTimeseries(), function(elem) {
                        if (elem.datasetType || elem.valueType) {
                            ids.push(elem.apiUrl + 'datasets/' + elem.id);
                        } else {
                            ids.push(elem.apiUrl + 'timeseries/' + elem.id);
                        }
                    });
                } else {
                    ids.push(timeseriesId);
                }
                return "ts=" + encodeURIComponent(ids.join());
            };
            var createTimeParam = function() {
                var timespan = statusService.getTime();
                return "timespan=" + encodeURIComponent(utils.createRequestTimespan(timespan.start, timespan.end));
            };
            var createBaseUrl = function(path) {
                if (path) {
                    return $location.absUrl().substring(0, $location.absUrl().indexOf($location.path())) + path + '?';
                } else {
                    return $location.absUrl().substring(0, $location.absUrl().indexOf('?')) + '?';
                }
            };
            this.createPermalink = function(path, params) {
                var parameterArray = [];
                for (var property in params) {
                    if (params.hasOwnProperty(property)) {
                        parameterArray.push(property + '=' + encodeURIComponent(params[property]));
                    }
                }
                return createBaseUrl(path) + parameterArray.join("&");
            };
            this.getCurrentPermalink = function(timeseriesId) {
                var params = [];
                // create timespan
                params.push(createTimeParam());
                // create id list
                params.push(createTimeseriesParam(timeseriesId));
                return createBaseUrl('/diagram') + params.join("&");
            };
        }
    ])
    .factory('permalinkEvaluationService', ['$location',
        function($location) {
            var parameters = $location.search();
            var hasParam = function(name) {
                return angular.isDefined(parameters[name]);
            };
            var getParam = function(name) {
                if (hasParam(name, parameters)) {
                    return parameters[name];
                }
                return null;
            };
            var getParameterArray = function(param) {
                var array = getParam(param);
                if (angular.isString(array)) {
                    return array.split(',');
                }
                return null;
            };
            return {
                hasParam: hasParam,
                getParam: getParam,
                getParameterArray: getParameterArray
            };
        }
    ]);

