angular.module('n52.core.helper')
        .factory('mapHelper', [
            function () {
                var adjustBounds = function (latlng, bounds) {
                    if (angular.isUndefined(bounds)) {
                        bounds = {
                            southWest: {
                                lat: latlng.lat,
                                lng: latlng.lng
                            },
                            northEast: {
                                lat: latlng.lat,
                                lng: latlng.lng
                            }
                        };
                    } else {
                        if (bounds.southWest.lat > latlng.lat) {
                            bounds.southWest.lat = latlng.lat;
                        }
                        if (bounds.southWest.lng > latlng.lng) {
                            bounds.southWest.lng = latlng.lng;
                        }
                        if (bounds.northEast.lat < latlng.lat) {
                            bounds.northEast.lat = latlng.lat;
                        }
                        if (bounds.northEast.lng < latlng.lng) {
                            bounds.northEast.lng = latlng.lng;
                        }
                    }
                    return bounds;
                };
                var createBounds = function (latlngs) {
                    var bounds;
                    angular.forEach(latlngs, function (latlng) {
                        bounds = adjustBounds(latlng, bounds);
                    });
                    return bounds;
                };

                return {
                    createBounds: createBounds
                };
            }]);

// this extension splits one getData request in a couple of requests if the supported timespan is to great
angular.module('n52.core.interface')
    .config(['$provide',
        function($provide) {
            $provide.decorator('seriesApiInterface', ['$delegate', '$q', '$http', 'utils',
                function($delegate, $q, $http, utils) {
                    var maxTimeExtent = moment.duration(365, 'day'),
                        promises;

                    $delegate.oldGetTsData = $delegate.getTsData;

                    $delegate.getTsData = function(id, apiUrl, timespan, extendedData, generalizeData) {
                        var params = {
                            generalize: generalizeData || false,
                            expanded: true,
                            format: 'flot'
                        };
                        if (extendedData) {
                            angular.extend(params, extendedData);
                        }

                        if ((timespan.end - timespan.start) > maxTimeExtent.asMilliseconds()) {
                            promises = [];
                            var start = moment(timespan.start);
                            while (start.isBefore(moment(timespan.end))) {
                                var step = moment(start).add(maxTimeExtent);
                                var promise = $delegate.getTsData(id, apiUrl, {
                                    start: start,
                                    end: step
                                }, extendedData, generalizeData);
                                promises.push(promise);
                                start = step;
                            }
                            return $q((resolve) => {
                                $q.all(promises).then((results) => {
                                    var data = results[0];
                                    for (var i = 1; i < results.length; i++) {
                                        data[id].values = data[id].values.concat(results[i][id].values);
                                    }
                                    resolve(data);
                                });
                            });
                        } else {
                            params.timespan = utils.createRequestTimespan(timespan.start, timespan.end);
                            return $delegate.oldGetTsData(id, apiUrl, timespan, params);
                        }
                    };

                    return $delegate;
                }
            ]);
        }
    ]);
